import { BottomNavigation } from "@material-ui/core";
import React from "react";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import { Doughnut } from "react-chartjs-2";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPaperPlane,
  faPlusCircle,
  faMinusCircle,
} from "@fortawesome/free-solid-svg-icons";

class LayoutOffers extends React.Component {
  render() {
    const dataDoughnutActives = {
      labels: ["Promoters", "Passives", "Detractors"],
      datasets: [
        {
          data: [300, 50, 300],
          backgroundColor: ["#16d2b4", "#ffd701", "#ff8167"],
          hoverBackgroundColor: ["#16d2b4", "#ffd701", "#ff8167"],
        },
      ],
    };
    const dataDoughnutNegatives = {
      labels: ["Offer", "Value", "Product", "Detractors"],
      datasets: [
        {
          data: [300, 50, 250, 50],
          backgroundColor: ["#1d46a0", "#2355c3", "#7493d9", "#afc1e8"],
          hoverBackgroundColor: ["#1d46a0", "#2355c3", "#7493d9", "#afc1e8"],
        },
      ],
    };

    return (
      <div>
        <div className="card-accepted-delivered">
          <Card
            style={{
              display: "block",
              width: "360px",
              backgroundColor: "#2355c3",
            }}
          >
            <h1
              style={{
                opacity: "0.5px",
                paddingTop: "20px",
                paddingLeft: "20px",
                fontSize: "26px",
                marginBottom: "10px",
                color: "whitesmoke",
                fontWeight: "300",
              }}
            >
              DELIVERED
            </h1>
            <div className="numbers-and-icon">
              <p
                style={{
                  textShadow: "1px 1px #666",
                  paddingLeft: "20px",
                  marginTop: "0px",
                  fontSize: "35px",
                  letterSpacing: "0px",
                  color: "whitesmoke",
                }}
              >
                979
              </p>
              <FontAwesomeIcon
                icon={faPaperPlane}
                size="7x"
                style={{
                  color: "#7493d9",
                  paddingLeft: "150px",
                  opacity: "0.3",
                }}
              ></FontAwesomeIcon>
            </div>
          </Card>
          <Card
            style={{
              display: "block",
              width: "360px",
              backgroundColor: "#16d2b4",
            }}
          >
            <h1
              style={{
                opacity: "0.5px",
                paddingTop: "20px",
                paddingLeft: "20px",
                fontSize: "26px",
                marginBottom: "10px",
                color: "whitesmoke",
                fontWeight: "300",
              }}
            >
              ACCEPTED
            </h1>
            <div className="numbers-and-icon">
              <p
                style={{
                  fontSize: "18px",
                  marginTop: "10px",
                  color: "whitesmoke",
                }}
              >
                515
              </p>
              <p
                style={{
                  fontSize: "18px",
                  marginTop: "10px",
                  color: "whitesmoke",
                }}
              >
                52.60%
              </p>
              <FontAwesomeIcon
                icon={faPlusCircle}
                size="7x"
                style={{
                  color: "#7493d9",
                  paddingLeft: "150px",
                }}
              ></FontAwesomeIcon>
            </div>
          </Card>
          <Card
            style={{
              display: "block",
              width: "360px",
              backgroundColor: "#ff8167",
            }}
          >
            <h1
              style={{
                opacity: "0.5px",
                paddingTop: "20px",
                paddingLeft: "20px",
                fontSize: "26px",
                marginBottom: "10px",
                color: "whitesmoke",
                fontWeight: "300",
              }}
            >
              DECLINED
            </h1>
            <div className="numbers-and-icon">
              <p
                style={{
                  fontSize: "18px",
                  marginTop: "10px",
                  color: "whitesmoke",
                }}
              >
                24
              </p>
              <p
                style={{
                  fontSize: "18px",
                  marginTop: "10px",
                  color: "whitesmoke",
                }}
              >
                2.45%
              </p>
              <FontAwesomeIcon
                icon={faMinusCircle}
                size="7x"
                style={{
                  color: "#7493d9",
                  paddingLeft: "150px",
                  opacity: "0.3",
                }}
              ></FontAwesomeIcon>
            </div>
          </Card>
        </div>
        <div className="charts-from-bottom">
          <div className="chart-from-bottom">
            <Card>
              <CardHeader title="Accepted Reviews Analysis" />
              <div style={{ width: 410 }}>
                <Doughnut
                  data={dataDoughnutActives}
                  options={{
                    rotation: 1 * Math.PI,
                    circumference: 1 * Math.PI,
                    legend: {
                      display: true,
                      position: "bottom",
                    },
                  }}
                />
              </div>
            </Card>
          </div>
          <div className="chart-from-bottomRight">
            <Card>
              <CardHeader title="Declined Reason Analysis" />
              <div style={{ width: 410, height: 205 }}>
                <Doughnut
                  data={dataDoughnutNegatives}
                  options={{
                    rotation: 1 * Math.PI,
                    circumference: 1 * Math.PI,
                    legend: {
                      display: true,
                      position: "bottom",
                    },
                  }}
                />
              </div>
            </Card>
          </div>
        </div>
      </div>
    );
  }
}

export default LayoutOffers;
