import React from "react";
import { Line } from "react-chartjs-2";

const state = {
  labels: [
    "2020-07-21",
    "2020-07-22",
    "2020-07-23",
    "2020-07-24",
    "2020-07-25",
  ],
  datasets: [
    {
      label: "Offers Accepted per day",
      fill: false,
      lineTension: 0.5,
      borderColor: "green",
      borderWidth: 3,
      data: [380, 70, 50, 50, 50],
    },
  ],
};

export default class ChartLine extends React.Component {
  render() {
    return (
      <div width="400" style={{ backgroundColor: "whitemsmoke" }}>
        <Line
          data={state}
          options={{
            title: {
              display: true,
              text: "Average Rainfall per month",
              fontSize: 20,
            },
            legend: {
              display: true,
              position: "right",
            },
          }}
        />
      </div>
    );
  }
}
