import React from "react";
import Card from "@material-ui/core/Card";
import LayoutCards from "./Layout-Cards";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTag,
  faGift,
  faMoneyCheckAlt,
} from "@fortawesome/free-solid-svg-icons";

class Layout extends React.Component {
  state = {
    mainPage: "main",
  };
  render() {
    return (
      <div className="layout-page">
        <nav className="nav-sidebar">
          <div>
            <h1 className="available-balance">Available balance:</h1>
            <h1 className="available-price">0.00€</h1>
            <hr></hr>
            <ul></ul>
            <div className="bullets-side-bar">
              <FontAwesomeIcon icon={faTag}></FontAwesomeIcon>
              <li
                style={{
                  padding: "20px 23px",
                  fontSize: "16px",
                  listStyleType: "none",
                }}
              >
                Brands
              </li>
            </div>
            <div className="bullets-side-bar">
              <FontAwesomeIcon icon={faGift}></FontAwesomeIcon>
              <li
                style={{
                  padding: "20px 23px",
                  fontSize: "16px",
                  listStyleType: "none",
                }}
              >
                Campaigns
              </li>
            </div>
            <div className="bullets-side-bar">
              <FontAwesomeIcon icon={faMoneyCheckAlt}></FontAwesomeIcon>
              <li
                style={{
                  padding: "20px 20px",
                  fontSize: "16px",
                  listStyleType: "none",
                }}
              >
                Budget
              </li>
            </div>
          </div>
        </nav>
        {this.state.mainPage === "main" ? <LayoutCards></LayoutCards> : null}
        <div className="copyright">
          <p>
            Copyright © 2019{" "}
            <a className="modatta-site" href="https://www.modatta.org/">
              Modatta.
            </a>{" "}
            All rights reserved.
          </p>
        </div>
      </div>
    );
  }
}
export default Layout;
