import React from "react";
import Card from "@material-ui/core/Card";
import LayoutCards from "./Layout-Cards";
import LayoutOffers from "./Layout-Offers";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTag,
  faGift,
  faMoneyCheckAlt,
} from "@fortawesome/free-solid-svg-icons";
import { makeStyles } from "@material-ui/core/styles";
import ListSubheader from "@material-ui/core/ListSubheader";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import Collapse from "@material-ui/core/Collapse";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import DraftsIcon from "@material-ui/icons/Drafts";
import SendIcon from "@material-ui/icons/Send";
import ExpandLess from "@material-ui/icons/ExpandLess";
import ExpandMore from "@material-ui/icons/ExpandMore";
import StarBorder from "@material-ui/icons/StarBorder";

class Layout extends React.Component {
  state = {
    mainPage: "main",
  };
  changeMainPage = (props) => {
    return this.setState({
      mainPage: props,
    });
  };
  render() {
    const useStyles = makeStyles((theme) => ({
      root: {
        width: "100%",
        maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
      },
      nested: {
        paddingLeft: theme.spacing(4),
      },
    }));

    function NestedList() {
      const classes = useStyles();
      const [open, setOpen] = React.useState(true);
    }

    const handleClick = () => {
      setOpen(!open);
    };

    return (
      <div className="layout-page">
        <nav className="nav-sidebar">
          <div>
            <h1 className="available-balance">Available balance:</h1>
            <h1 className="available-price">0.00€</h1>
            <hr></hr>
            <ul></ul>
            <div className="bullets-side-bar">
              <FontAwesomeIcon icon={faTag}></FontAwesomeIcon>
              <a
                href="javascript:void(0)"
                onClick={() => this.changeMainPage("brands")}
              >
                <li
                  style={{
                    padding: "20px 23px",
                    fontSize: "16px",
                    listStyleType: "none",
                  }}
                >
                  Brands
                </li>
              </a>
            </div>
            <div className="bullets-side-bar">
              <FontAwesomeIcon icon={faGift}></FontAwesomeIcon>
              <a href="javascript:void(0)">
                <li
                  style={{
                    padding: "20px 23px",
                    fontSize: "16px",
                    listStyleType: "none",
                  }}
                >
                  Campaigns
                </li>
              </a>
            </div>
            <div className="bullets-side-bar">
              <FontAwesomeIcon icon={faMoneyCheckAlt}></FontAwesomeIcon>
              <a href="javascript:void(0)">
                {" "}
                <li
                  style={{
                    padding: "20px 20px",
                    fontSize: "16px",
                    listStyleType: "none",
                  }}
                >
                  Budget
                </li>
              </a>
            </div>
            <List
              component="nav"
              aria-labelledby="nested-list-subheader"
              subheader={
                <ListSubheader component="div" id="nested-list-subheader">
                  Nested List Items
                </ListSubheader>
              }
              className={classes.root}
            >
              <ListItem button>
                <ListItemIcon>
                  <SendIcon />
                </ListItemIcon>
                <ListItemText primary="Sent mail" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <DraftsIcon />
                </ListItemIcon>
                <ListItemText primary="Drafts" />
              </ListItem>
              <ListItem button onClick={handleClick}>
                <ListItemIcon>
                  <InboxIcon />
                </ListItemIcon>
                <ListItemText primary="Inbox" />
                {open ? <ExpandLess /> : <ExpandMore />}
              </ListItem>
              <Collapse in={open} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  <ListItem button className={classes.nested}>
                    <ListItemIcon>
                      <StarBorder />
                    </ListItemIcon>
                    <ListItemText primary="Starred" />
                  </ListItem>
                </List>
              </Collapse>
            </List>
            ); }
          </div>
        </nav>
        <div>
          {this.state.mainPage === "main" ? (
            <LayoutCards></LayoutCards>
          ) : (
            <LayoutOffers></LayoutOffers>
          )}
          <div className="copyright">
            <p>
              Copyright © 2019{" "}
              <a className="modatta-site" href="https://www.modatta.org/">
                Modatta.
              </a>{" "}
              All rights reserved.
            </p>
          </div>
        </div>
      </div>
    );
  }
}
export default Layout;
