import { BottomNavigation } from "@material-ui/core";
import React from "react";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import { Doughnut } from "react-chartjs-2";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPaperPlane,
  faPlusCircle,
  faMinusCircle,
} from "@fortawesome/free-solid-svg-icons";

class LayoutOffers extends React.Component {
  state = {
    openDialogue: false,
  };
  handleClickOpen = () => {
    this.setState({
      openDialogue: true,
    });
  };
  handleClose = () => {
    this.setState({
      openDialogue: false,
    });
  };
  render() {
    const dataDoughnutActives = {
      labels: ["Promoters", "Passives", "Detractors"],
      datasets: [
        {
          data: [300, 50, 300],
          backgroundColor: ["#16d2b4", "#ffd701", "#ff8167"],
          hoverBackgroundColor: ["#16d2b4", "#ffd701", "#ff8167"],
        },
      ],
    };
    const dataDoughnutNegatives = {
      labels: ["Offer", "Value", "Product", "Detractors"],
      datasets: [
        {
          data: [300, 50, 250, 50],
          backgroundColor: ["#1d46a0", "#2355c3", "#7493d9", "#afc1e8"],
          hoverBackgroundColor: ["#1d46a0", "#2355c3", "#7493d9", "#afc1e8"],
        },
      ],
    };

    return (
      <div>
        <div className="infos">
          <Dialog
            open={open}
            onClose={handleClose}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
          >
            <DialogTitle id="alert-dialog-title">
              {"Use Google's location service?"}
            </DialogTitle>
            <DialogContent>
              <DialogContentText id="alert-dialog-description">
                Let Google help apps determine location. This means sending
                anonymous location data to Google, even when no apps are
                running.
              </DialogContentText>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose} color="primary">
                Disagree
              </Button>
              <Button onClick={handleClose} color="primary" autoFocus>
                Agree
              </Button>
            </DialogActions>
          </Dialog>
          <Card
            style={{
              display: "block",
              width: "330px",
            }}
            onClick={this.handleClickOpen}
          >
            <CardHeader
              title="Status"
              style={{
                backgroundColor: "#6674",
              }}
            />
            <h1
              style={{
                fontSize: "20px",
              }}
            >
              Offer Offline
            </h1>
          </Card>
          <Card
            style={{
              display: "block",
              width: "330px",
            }}
          >
            <CardHeader
              title="Accumulated Cost"
              style={{
                backgroundColor: "#6674",
              }}
            />
            <h1
              style={{
                fontSize: "20px",
              }}
            >
              10.65€
            </h1>
          </Card>
          <Card
            style={{
              display: "block",
              width: "330px",
            }}
          >
            <CardHeader
              title="NPS"
              style={{
                backgroundColor: "#6674",
              }}
            />
            <h1
              style={{
                fontSize: "20px",
              }}
            >
              32
            </h1>
          </Card>
        </div>
        <div className="card-accepted-delivered">
          <Card
            style={{
              display: "block",
              width: "330px",
              backgroundColor: "#2355c3",
            }}
          >
            <h1
              style={{
                opacity: "0.5px",
                paddingTop: "20px",
                paddingLeft: "20px",
                fontSize: "26px",
                marginBottom: "10px",
                color: "whitesmoke",
                fontWeight: "300",
              }}
            >
              DELIVERED
            </h1>
            <div className="numbers-and-icon">
              <p
                style={{
                  paddingLeft: "20px",
                  marginTop: "0px",
                  fontSize: "30px",
                  letterSpacing: "0px",
                  color: "whitesmoke",
                }}
              >
                979
              </p>
              <FontAwesomeIcon
                icon={faPaperPlane}
                size="7x"
                style={{
                  color: "#7493d9",
                  paddingLeft: "130px",
                  opacity: "0.3",
                }}
              ></FontAwesomeIcon>
            </div>
          </Card>
          <Card
            style={{
              display: "block",
              width: "330px",
              backgroundColor: "#16d2b4",
            }}
          >
            <h1
              style={{
                opacity: "0.5px",
                paddingTop: "20px",
                paddingLeft: "20px",
                fontSize: "26px",
                marginBottom: "10px",
                color: "whitesmoke",
                fontWeight: "300",
              }}
            >
              ACCEPTED
            </h1>
            <div className="numbers-and-icon">
              <div className="number-greenCard">
                <p
                  style={{
                    paddingLeft: "20px",
                    fontSize: "30px",
                    letterSpacing: "0px",
                    color: "whitesmoke",
                    marginTop: "0px",
                    marginBottom: "0px",
                  }}
                >
                  515
                </p>
                <p
                  style={{
                    paddingLeft: "20px",
                    marginTop: "0px",
                    fontSize: "25px",
                    letterSpacing: "0px",
                    color: "whitesmoke",
                  }}
                >
                  52.60%
                </p>
              </div>
              <FontAwesomeIcon
                icon={faPlusCircle}
                size="7x"
                style={{
                  color: "#7493d9",
                  paddingLeft: "100px",
                  opacity: "0.5",
                }}
              ></FontAwesomeIcon>
            </div>
          </Card>
          <Card
            style={{
              display: "block",
              width: "330px",
              backgroundColor: "#ff8167",
              zIndex: "1",
            }}
          >
            <h1
              style={{
                opacity: "0.5px",
                paddingTop: "20px",
                paddingLeft: "20px",
                fontSize: "26px",
                marginBottom: "10px",
                color: "whitesmoke",
                fontWeight: "300",
              }}
            >
              DECLINED
            </h1>
            <div className="numbers-and-icon">
              <div className="number-greenCard">
                <p
                  style={{
                    paddingLeft: "20px",
                    marginTop: "0px",
                    fontSize: "30px",
                    letterSpacing: "0px",
                    color: "whitesmoke",
                    marginBottom: "0px",
                  }}
                >
                  24
                </p>
                <p
                  style={{
                    paddingLeft: "20px",
                    marginTop: "0px",
                    fontSize: "25px",
                    letterSpacing: "0px",
                    color: "whitesmoke",
                  }}
                >
                  2.45%
                </p>
              </div>
              <FontAwesomeIcon
                icon={faMinusCircle}
                size="7x"
                style={{
                  color: "#7493d9",
                  paddingLeft: "115px",
                  opacity: "0.5",
                  top: "100px",
                }}
              ></FontAwesomeIcon>
            </div>
          </Card>
        </div>
        <div className="charts-from-bottom">
          <div className="chart-from-bottom">
            <Card>
              <CardHeader title="Accepted Reviews Analysis" />
              <div style={{ width: 510, height: 205 }}>
                <Doughnut
                  data={dataDoughnutActives}
                  options={{
                    maintainAspectRatio: false,
                    rotation: 1 * Math.PI,
                    circumference: 1 * Math.PI,
                    legend: {
                      display: true,
                      position: "bottom",
                    },
                  }}
                />
              </div>
            </Card>
          </div>
          <div className="chart-from-bottomRight">
            <Card>
              <CardHeader title="Declined Reason Analysis" />
              <div style={{ width: 510, height: 205 }}>
                <Doughnut
                  data={dataDoughnutNegatives}
                  options={{
                    maintainAspectRatio: false,
                    rotation: 1 * Math.PI,
                    circumference: 1 * Math.PI,
                    legend: {
                      display: true,
                      position: "bottom",
                    },
                  }}
                />
              </div>
            </Card>
          </div>
        </div>
      </div>
    );
  }
}

export default LayoutOffers;
